mapper.select:
select *
from user
where userId = #{userId} and password = #{password}

select userid, userpwd, username, email, address, date_format(joindate, '%y.%m.%d') joindate
from ssafy_member
order by joindate desc;

select count(*)
from user
where userId = #{userId}

select count(*)
from user
where nickname = #{nickname}

mapper.update:
update user
set profileImg=#{profileImg},nickname=#{nickname},homeAlone=#{homeAlone}, intro=#{intro}
where userId = #{userId}

update user
set addr=#{addr}
where userId = #{userId}

mapper.select:
select count(*)
from board
where userId = #{userId}
